mapper.select:
SELECT r.*, g.name AS goodsName, gt.name AS goodstypeName, s.name AS storeName, u1.name as userName, u2.name as adminName FROM tb_record r LEFT JOIN tb_goods g ON r.goods = g.id LEFT JOIN tb_goodstype gt ON gt.id = g.goods_type LEFT JOIN tb_store s ON s.id = g.store left join tb_user u1 on u1.id = r.userId left join tb_user u2 on u2.id = r.admin_id
AND g.name LIKE CONCAT('%', #{name}, '%')
AND gt.id = #{goodstype}
AND s.id = #{store}
AND r.userId = #{userId}
